#!/usr/bin/env python
def add(a=0,b=0):
    if a:
        if b:
            try:
                c=a+b
            except ValueError:
                print("You can't write!")
            else:
                return c
        else:
            print('You did not enter "b"!')
    else:
        print('You did not enter "a"!')
